const getSelectedDays = (creneauxIds, tousLesCreneaux) => {
  let days = []
  for (let i = 0; i < creneauxIds.length; i++) {
    const item = creneauxIds[i];
    for (let j = 0; j < tousLesCreneaux.length; j++) {
      const element = tousLesCreneaux[j];
      if (item == element.id ) {
        days.push(element.day)
      }
    }
  }
  days = [...new Set(days)]
  return days
}

